import "./Chat.css";
import { useLocation } from "react-router";
import { useState } from "react";
import { io } from "socket.io-client";

var socket = io("http://localhost:8080", {
  transports: ["websocket"],
});

export default function Chat(props) {
  const location = useLocation();
  const { userName } = location.state;
  const [text, setText] = useState("");
  const [message, setMessage] = useState("");

  socket.on("chat_text", function (msg) {
    console.log(msg);
    setMessage(msg);
  });

  const handleChange = (event) => {
    setText(event.target.value);
  };

  const send = (event) => {
    event.preventDefault();
    socket.emit("chat_message", { text, userName });
    setText("");
  };
  return (
    <div className='chat_container'>
      <div className='header'>
        <img className='avatarImg' alt='user_avatar' src='../avatr1.png' />
        <p className='user_name'>{userName}</p>
      </div>
      <p>{message}</p>
      <div className='footer'>
        <form className='input_container'>
          <input value={text} onChange={handleChange} className='chat_input' />
          <button onClick={send} className='chat_button'>
            SEND!
          </button>
        </form>
      </div>
    </div>
  );
}
